import React, { useState } from "react";

const Transactions = ({ user, transactions, updateBalance }) => {
  const [newTransaction, setNewTransaction] = useState({
    description: "",
    amount: "",
  });

  const handleTransaction = (e) => {
    e.preventDefault();
    const amount = parseFloat(newTransaction.amount);

    if (isNaN(amount)) {
      alert("Please enter a valid amount.");
      return;
    }

    const updatedBalance = user.balance + amount; // Deduct or add based on positive/negative
    if (updatedBalance < 0) {
      alert("Insufficient balance!");
      return;
    }

    // Update balance and reset transaction form
    updateBalance(updatedBalance);
    setNewTransaction({ description: "", amount: "" });
  };

  return (
    <div className="container">
      <h2 className="mb-4">Welcome, {user.name}!</h2> {/* Welcome message */}
      <p className="lead">Current Balance: ${user.balance.toFixed(2)}</p>
      <form onSubmit={handleTransaction}>
        <div className="mb-3">
          <input
            type="text"
            placeholder="Description"
            value={newTransaction.description}
            onChange={(e) =>
              setNewTransaction({ ...newTransaction, description: e.target.value })
            }
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <input
            type="number"
            placeholder="Amount (negative for withdrawal)"
            value={newTransaction.amount}
            onChange={(e) =>
              setNewTransaction({ ...newTransaction, amount: e.target.value })
            }
            className="form-control"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Perform Transaction
        </button>
      </form>

      <h3 className="mt-4">Transaction History</h3>
      <ul className="list-group">
        {transactions.map((t, index) => (
          <li key={index} className="list-group-item">
            {t.date} - {t.description}: ${t.amount}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Transactions;
